    <script>
        Zepto(function($) {

            $('body').addClass('loading');

            // loading完成前阻止拖动
            $('#mask').on('touchstart touchmove touchend touchcancel', function(e) {
                e.preventDefault();
                e.stopPropagation();
                return false;
            });

            // 加载完成
            $(window).on('load', function(e) {

                // 距离配置
                var disCfg = {
                    ranking: 4500
                };

                window.mySkrollr = skrollr.init({
                    smoothScrolling: false,
                    smoothScrollingDuration: 200,
                    mobileDeceleration: 0.5,
                    constants: {
                        title: 0,
                        records: 1000,
                        ranking: disCfg.ranking,
                        ranking02: disCfg.ranking + 1250,
                        ranking03: disCfg.ranking + 1250 * 2,
                        ranking04: disCfg.ranking + 1250 * 3,
                        ranking05: disCfg.ranking + 1250 * 4,
                        ranking06: disCfg.ranking + 1250 * 5,
                        ranking07: disCfg.ranking + 1250 * 6,
                        ranking08: disCfg.ranking + 1250 * 7
                    },
                    keyframe: function(element, name, direction) {
                        // name will be one of data500, dataTopBottom, data_offsetCenter
                        var node = $(element);
                        if (node && node.length) {
                            var cls = node.attr('data-lazyload');
                            var imgs = $('.' + cls);
                            $.each(imgs, function(key, img) {
                                var imgNode = $(img);
                                imgNode.attr('src', imgNode.attr('data-src'));
                            });
                        }
                    }
                });

                // 显示
                $('body').removeClass('loading').addClass('ready');

                setTimeout(function(){
                    window.mySkrollr.animateTo(800, {
                        duration: 2000,
                        easing: 'swing',
                        done: function() {
                            $('#arrow').show();
                            $('#loading02').hide();
                            $('#mask').remove();
                        }
                    })
                }, 3000);
                
            });

        });
    </script>
